import numpy as npclass GameOfLife:        # TODO: check iniFrame        def __init__(self, initial_config: np.ndarray, size: tuple, n_frames: int, position: tuple) -> None:        self.initial_config = initial_config        self.size = size        self.n_frames = n_frames        self.position = position        self.grid = self._init_grid()            def _init_grid(self, size: tuple):        cells = np.zeros(size)        cells[self.position[0]:self.position[0] + self.initial_config.shape[0],               self.position[1]:self.position[1] + self.initial_config.shape[1]] = self.initial_config                return cells