#!/usr/bin/pythonimport sysimport getoptfrom enum import Enumclass GameType(Enum):    GAME_OF_LIFE=1    EVO_GAME=1    def generate_experiment(argv):         try:       opts, args = getopt.getopt(argv, shortopts="", longopts=['game_type=', 'exp_folder_name='])           except getopt.GetoptError as e:       print('Error: ', e)       sys.exit(2)           for opt, arg in opts:        if opt not in ('--game_type', '--exp_folder_name'):            print('Invalid parameter')            sys.exit()        elif opt == '--game_type':            game_type = arg        elif opt == '--exp_folder_name':            exp_folder = arg                print(f'gt: {game_type}, ef: {exp_folder}')if __name__ == "__main__":   generate_experiment(sys.argv[1:])